// Extract and format content for Telegram
const input = $input.first();
const content = input.json.content || input.json.text || '';
const title = input.json.title || 'Content Update';
const sourceUrl = input.json.sourceUrl || input.json.url || '';

// Format content for Telegram (4096 char limit)
let telegramMessage = `ðŸ“¢ *${title.replace(/[_*\[\]()~`>#+=|{}.!-]/g, '\\$&')}*\n\n`;

// Smart truncation - preserve URLs and key information
const maxContentLength = 4000 - telegramMessage.length - (sourceUrl ? sourceUrl.length + 20 : 0);

let contentText = content;
if (content.length > maxContentLength) {
  // Find the last complete sentence within limit
  const truncated = content.substring(0, maxContentLength);
  const lastSentence = truncated.lastIndexOf('.');
  const lastNewline = truncated.lastIndexOf('\n');
  const cutPoint = Math.max(lastSentence, lastNewline);

  contentText = cutPoint > maxContentLength * 0.7 ? 
    truncated.substring(0, cutPoint + 1) : 
    truncated + '...';
}

telegramMessage += contentText;

// Add source URL if available
if (sourceUrl) {
  telegramMessage += `\n\nðŸ”— [Source](${sourceUrl})`;
}

// Return formatted data for both chat IDs
const chatIds = ['425347269', '-1002339513336'];
const messages = chatIds.map(chatId => ({
  chatId: chatId,
  text: telegramMessage,
  originalContent: content,
  truncated: content.length > maxContentLength
}));

return messages;

// Log broadcast results and return summary
const results = $input.all();
const successful = results.filter(r => r.json.ok).length;
const failed = results.length - successful;

console.log(`Telegram broadcast completed: ${successful} successful, ${failed} failed`);

results.forEach(result => {
  if (result.json.ok) {
    console.log(`âœ… Message sent to chat ${result.json.result.chat.id}`);
  } else {
    console.log(`âŒ Failed to send to chat: ${result.json.description}`);
  }
});

return [{
  broadcastId: Date.now(),
  successful: successful,
  failed: failed,
  timestamp: new Date().toISOString(),
  details: results.map(r => ({
    chatId: r.json.result?.chat?.id || 'unknown',
    success: r.json.ok,
    messageId: r.json.result?.message_id,
    error: r.json.description
  }))
}];
